mapper.select:
select

from
 t_user

  user_status = '1'


    and user_name like #{userNamePatten}


limit #{start}, #{length}

select count(user_id) from t_user
user_status = '1'
and user_name = #{userNamePatten}

mapper.insert:
insert into t_user (user_id, user_name, user_email, 
  user_phone, user_account, user_password, 
  create_time, user_status, login_time, 
  last_login_time, login_location, login_ip_address
  )
values (#{userId,jdbcType=INTEGER}, #{userName,jdbcType=VARCHAR}, #{userEmail,jdbcType=VARCHAR}, 
  #{userPhone,jdbcType=VARCHAR}, #{userAccount,jdbcType=VARCHAR}, #{userPassword,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{userStatus,jdbcType=VARCHAR}, #{loginTime,jdbcType=TIMESTAMP}, 
  #{lastLoginTime,jdbcType=TIMESTAMP}, #{loginLocation,jdbcType=VARCHAR}, #{loginIpAddress,jdbcType=VARCHAR}
  )
